              source = {require("../mount.jpeg")}>
                <Text style={someStyles.text}> Donnolis</Text>


                <View style={{ flex: 1, alignItems: 'left ',flexDirection:'row' }}>
                  <Image style= {someStyles.viewImage}
                    source={require("../avatar.jpeg")} 
                    >
                  </Image>
