              </View>


              <Carousel style={{flex:4, alignSelf:'center'}} >
                <Carousel.Item style={{marginTop:40}}>
                  <Image style={someStyles.carouselImage}
                      source={require('../imageNYC.jpeg')}
                        alt="First slide"
                  />
