                    <p1>This is a setting</p1>
                  </Carousel.Caption>


                </Carousel.Item>


                 <Carousel.Item style={{marginTop:40}}>
                  <Image style={someStyles.carouselImage}
                      source={require('../imageNLP.jpeg')}
                        alt="First slide"
                  />

                </Carousel.Item>

                </Carousel>

            </ImageBackground>             
          </View>

            </ScrollView>

        </SafeAreaView>

  );
}
